USUARIO=""
USUARIO=$(echo $USUARIO|desencripta)
PASS=""
PASS=$(echo $PASS | desencripta)
SUPERPASS=""
SUPERPASS=$(echo $SUPERPASS | desencripta)
if [ "$1" = "bye" ]; then
   echo nat natp delete https | xclip
elif [ "$1" = "super" ]; then 
   echo $SUPERPASS | xclip   
else 
   echo nat natp add https Internet ALL 443:443 443:443 192.168.1.27 | xclip
fi
sshpass -p $PASS ssh $USUARIO@r1
